import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './login.component.html',
  styleUrl: './login.component.css'
})
export class LoginComponent {
  isLoginMode = true;
  loading = false;
  errorMessage = '';

  loginForm = {
    email: '',
    password: ''
  };

  registerForm = {
    name: '',
    email: '',
    password: '',
    confirmPassword: ''
  };

  constructor(
    private authService: AuthService,
    private router: Router
  ) {}

  toggleMode(): void {
    this.isLoginMode = !this.isLoginMode;
    this.errorMessage = '';
    this.loginForm = { email: '', password: '' };
    this.registerForm = { name: '', email: '', password: '', confirmPassword: '' };
  }

  login(): void {
    if (!this.loginForm.email || !this.loginForm.password) {
      this.errorMessage = 'Por favor, preencha todos os campos';
      return;
    }

    this.loading = true;
    this.errorMessage = '';

    this.authService.login(this.loginForm).subscribe({
      next: () => {
        this.router.navigate(['/dashboard']);
      },
      error: (error) => {
        this.errorMessage = error.error?.error || 'Erro ao fazer login';
        this.loading = false;
      }
    });
  }

  register(): void {
    if (!this.registerForm.name || !this.registerForm.email || !this.registerForm.password) {
      this.errorMessage = 'Por favor, preencha todos os campos';
      return;
    }

    if (this.registerForm.password !== this.registerForm.confirmPassword) {
      this.errorMessage = 'As senhas não coincidem';
      return;
    }

    if (this.registerForm.password.length < 6) {
      this.errorMessage = 'A senha deve ter no mínimo 6 caracteres';
      return;
    }

    this.loading = true;
    this.errorMessage = '';

    this.authService.register({
      name: this.registerForm.name,
      email: this.registerForm.email,
      password: this.registerForm.password
    }).subscribe({
      next: () => {
        // Após registro, fazer login automaticamente
        this.authService.login({
          email: this.registerForm.email,
          password: this.registerForm.password
        }).subscribe({
          next: () => {
            this.router.navigate(['/dashboard']);
          },
          error: (error) => {
            this.errorMessage = 'Registro realizado, mas erro ao fazer login: ' + (error.error?.error || error.message);
            this.loading = false;
          }
        });
      },
      error: (error) => {
        this.errorMessage = error.error?.error || 'Erro ao registrar';
        this.loading = false;
      }
    });
  }
}

<div class="login-container">
  <div class="login-card">
    <div class="login-header">
      <h1>{{ isLoginMode ? 'Bem-vindo' : 'Criar Conta' }}</h1>
      <p>{{ isLoginMode ? 'Faça login para continuar' : 'Preencha os dados para criar sua conta' }}</p>
    </div>

    <div class="alert alert-error" *ngIf="errorMessage">
      {{ errorMessage }}
    </div>

    <form (ngSubmit)="isLoginMode ? login() : register()" class="login-form">
      <div *ngIf="!isLoginMode" class="form-group">
        <label for="name">Nome</label>
        <input
          type="text"
          id="name"
          [(ngModel)]="registerForm.name"
          name="name"
          placeholder="Digite seu nome"
          required
        />
      </div>

      <div class="form-group">
        <label for="email">Email</label>
        <input
          type="email"
          id="email"
          [(ngModel)]="isLoginMode ? loginForm.email : registerForm.email"
          [name]="isLoginMode ? 'loginEmail' : 'registerEmail'"
          placeholder="Digite seu email"
          required
        />
      </div>

      <div class="form-group">
        <label for="password">Senha</label>
        <input
          type="password"
          id="password"
          [(ngModel)]="isLoginMode ? loginForm.password : registerForm.password"
          [name]="isLoginMode ? 'loginPassword' : 'registerPassword'"
          placeholder="Digite sua senha"
          required
        />
      </div>

      <div *ngIf="!isLoginMode" class="form-group">
        <label for="confirmPassword">Confirmar Senha</label>
        <input
          type="password"
          id="confirmPassword"
          [(ngModel)]="registerForm.confirmPassword"
          name="confirmPassword"
          placeholder="Confirme sua senha"
          required
        />
      </div>

      <button type="submit" class="btn btn-primary btn-block" [disabled]="loading">
        <span *ngIf="!loading">{{ isLoginMode ? 'Entrar' : 'Criar Conta' }}</span>
        <span *ngIf="loading">Carregando...</span>
      </button>
    </form>

    <div class="login-footer">
      <p>
        {{ isLoginMode ? 'Não tem uma conta?' : 'Já tem uma conta?' }}
        <a (click)="toggleMode()" class="link">{{ isLoginMode ? 'Criar conta' : 'Fazer login' }}</a>
      </p>
    </div>
  </div>
</div>
