import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { UserService } from '../../services/user.service';
import { AuthService } from '../../services/auth.service';
import { User } from '../../models/user.model';
import { 
  fadeInOut, 
  slideUp, 
  slideIn, 
  staggerSlideIn, 
  bounceIn, 
  fadeInScale,
  slideDown,
  scaleIn
} from '../../animations';

@Component({
  selector: 'app-dashboard',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './dashboard.component.html',
  styleUrl: './dashboard.component.css',
  animations: [fadeInOut, slideUp, slideIn, staggerSlideIn, bounceIn, fadeInScale, slideDown, scaleIn]
})
export class DashboardComponent implements OnInit {
  users: User[] = [];
  currentUser: User | null = null;
  showModal = false;
  showProfileModal = false;
  isEditing = false;
  errorMessage = '';
  successMessage = '';
  currentUserInfo: any;
  currentUserProfile: User | null = null;
  loading = false;
  loadingProfile = false;
  deletingUserId: number | null = null;

  userForm = {
    name: '',
    email: ''
  };

  constructor(
    private userService: UserService,
    private authService: AuthService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.currentUserInfo = this.authService.getUser();
    this.loadUsers();
  }

  loadUsers(): void {
    this.loading = true;
    this.userService.getAllUsers().subscribe({
      next: (users) => {
        this.users = users;
        this.clearMessages();
        this.loading = false;
      },
      error: (error) => {
        this.loading = false;
        if (error.status === 401) {
          this.authService.logout();
          this.router.navigate(['/login']);
        } else {
          this.showError('Erro ao carregar usuários: ' + (error.error?.error || error.message));
        }
      }
    });
  }

  openCreateModal(): void {
    this.isEditing = false;
    this.currentUser = null;
    this.userForm = { name: '', email: '' };
    this.showModal = true;
    this.clearMessages();
  }

  openEditModal(user: User): void {
    this.isEditing = true;
    this.currentUser = user;
    this.userForm = { name: user.name, email: user.email };
    this.showModal = true;
    this.clearMessages();
  }

  closeModal(): void {
    this.showModal = false;
    this.currentUser = null;
    this.userForm = { name: '', email: '' };
    this.clearMessages();
  }

  saveUser(): void {
    if (!this.userForm.name || !this.userForm.email) {
      this.showError('Por favor, preencha todos os campos');
      return;
    }

    if (this.isEditing && this.currentUser) {
      this.userService.updateUser(this.currentUser.id, this.userForm).subscribe({
        next: () => {
          this.showSuccess('Usuário atualizado com sucesso!');
          this.closeModal();
          this.loadUsers();
        },
        error: (error) => {
          if (error.status === 401) {
            this.authService.logout();
            this.router.navigate(['/login']);
          } else {
            this.showError('Erro ao atualizar usuário: ' + (error.error?.error || error.message));
          }
        }
      });
    } else {
      this.userService.createUser(this.userForm).subscribe({
        next: () => {
          this.showSuccess('Usuário criado com sucesso!');
          this.closeModal();
          this.loadUsers();
        },
        error: (error) => {
          if (error.status === 401) {
            this.authService.logout();
            this.router.navigate(['/login']);
          } else {
            this.showError('Erro ao criar usuário: ' + (error.error?.error || error.message));
          }
        }
      });
    }
  }

  deleteUser(id: number): void {
    if (confirm('Tem certeza que deseja excluir este usuário?')) {
      this.deletingUserId = id;
      this.userService.deleteUser(id).subscribe({
        next: () => {
          this.deletingUserId = null;
          this.showSuccess('Usuário excluído com sucesso!');
          this.loadUsers();
        },
        error: (error) => {
          this.deletingUserId = null;
          if (error.status === 401) {
            this.authService.logout();
            this.router.navigate(['/login']);
          } else {
            this.showError('Erro ao excluir usuário: ' + (error.error?.error || error.message));
          }
        }
      });
    }
  }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/login']);
  }

  showError(message: string): void {
    this.errorMessage = message;
    this.successMessage = '';
    setTimeout(() => this.clearMessages(), 5000);
  }

  showSuccess(message: string): void {
    this.successMessage = message;
    this.errorMessage = '';
    setTimeout(() => this.clearMessages(), 5000);
  }

  clearMessages(): void {
    this.errorMessage = '';
    this.successMessage = '';
  }
}

<div class="dashboard-container">
  <header class="dashboard-header">
    <div class="header-content">
      <div>
        <h1>CRUD de Usuários</h1>
        <p class="subtitle">Gerenciamento completo de usuários</p>
      </div>
      <div class="user-info">
        <span class="user-name">{{ currentUserInfo?.name }}</span>
        <button class="btn btn-logout" (click)="logout()">
          <span class="icon">🚪</span> Sair
        </button>
      </div>
    </div>
  </header>

  <div class="dashboard-content">
    <div class="alert alert-error" *ngIf="errorMessage" [@fadeInOut]>
      <span class="alert-icon">⚠️</span>
      {{ errorMessage }}
    </div>

    <div class="alert alert-success" *ngIf="successMessage" [@fadeInOut]>
      <span class="alert-icon">✓</span>
      {{ successMessage }}
    </div>

    <div class="card card-header" [@slideDown]>
      <div class="card-header-content">
        <div>
          <h2>Usuários Cadastrados</h2>
          <p class="card-subtitle">{{ users.length }} usuário(s) no total</p>
        </div>
        <button class="btn btn-primary btn-icon ripple" (click)="openCreateModal()">
          <span class="icon">+</span> Novo Usuário
        </button>
      </div>
    </div>

    <div class="loading-container" *ngIf="loading" [@fadeInScale]>
      <div class="spinner"></div>
      <p>Carregando usuários...</p>
    </div>

    <div class="card" *ngIf="users.length > 0 && !loading; else noUsers" [@fadeInScale]>
      <div class="table-container">
        <table class="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Nome</th>
              <th>Email</th>
              <th class="actions-column">Ações</th>
            </tr>
          </thead>
          <tbody [@staggerSlideIn]="users">
            <tr *ngFor="let user of users; let i = index" 
                [@slideIn] 
                class="table-row"
                [class.deleting]="deletingUserId === user.id">
              <td class="id-cell">{{ user.id }}</td>
              <td class="name-cell">{{ user.name }}</td>
              <td class="email-cell">{{ user.email }}</td>
              <td class="actions-cell">
                <button class="btn btn-icon btn-edit ripple" 
                        (click)="openEditModal(user)" 
                        title="Editar"
                        [disabled]="deletingUserId === user.id">
                  <span class="icon">✏️</span>
                </button>
                <button class="btn btn-icon btn-delete ripple" 
                        (click)="deleteUser(user.id)" 
                        title="Excluir"
                        [disabled]="deletingUserId === user.id">
                  <span class="icon" *ngIf="deletingUserId !== user.id">🗑️</span>
                  <span class="spinner-small" *ngIf="deletingUserId === user.id"></span>
                </button>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>

    <ng-template #noUsers>
      <div class="card empty-state" *ngIf="!loading" [@bounceIn]>
        <div class="empty-state-content">
          <span class="empty-icon" [@rotateIn]>👥</span>
          <h3>Nenhum usuário cadastrado</h3>
          <p>Comece criando seu primeiro usuário</p>
          <button class="btn btn-primary ripple" (click)="openCreateModal()">
            + Criar Primeiro Usuário
          </button>
        </div>
      </div>
    </ng-template>
  </div>
</div>

<!-- Modal -->
<div class="modal" [class.show]="showModal" (click)="closeModal()" [@fadeInOut]>
  <div class="modal-content" (click)="$event.stopPropagation()" [@slideUp]>
    <div class="modal-header">
      <h2 [@scaleIn]>{{ isEditing ? 'Editar Usuário' : 'Novo Usuário' }}</h2>
      <button class="close ripple" (click)="closeModal()" [@rotateIn]>&times;</button>
    </div>

    <form (ngSubmit)="saveUser()">
      <div class="form-group" [@fadeInScale]>
        <label for="name">Nome</label>
        <input
          type="text"
          id="name"
          [(ngModel)]="userForm.name"
          name="name"
          placeholder="Digite o nome"
          required
        />
      </div>

      <div class="form-group" [@fadeInScale]>
        <label for="email">Email</label>
        <input
          type="email"
          id="email"
          [(ngModel)]="userForm.email"
          name="email"
          placeholder="Digite o email"
          required
        />
      </div>

      <div class="modal-footer" [@fadeInScale]>
        <button type="button" class="btn btn-secondary ripple" (click)="closeModal()">
          Cancelar
        </button>
        <button type="submit" class="btn btn-primary ripple">
          {{ isEditing ? 'Atualizar' : 'Criar' }}
        </button>
      </div>
    </form>
  </div>
</div>
