import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { UserService } from './services/user.service';
import { User } from './models/user.model';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './app.component.html',
  styleUrl: './app.component.css'
})
export class AppComponent implements OnInit {
  users: User[] = [];
  currentUser: User | null = null;
  showModal = false;
  isEditing = false;
  errorMessage = '';
  successMessage = '';

  userForm = {
    name: '',
    email: ''
  };

  constructor(private userService: UserService) {}

  ngOnInit(): void {
    this.loadUsers();
  }

  loadUsers(): void {
    this.userService.getAllUsers().subscribe({
      next: (users) => {
        this.users = users;
        this.clearMessages();
      },
      error: (error) => {
        this.showError('Erro ao carregar usuários: ' + (error.error?.error || error.message));
      }
    });
  }

  openCreateModal(): void {
    this.isEditing = false;
    this.currentUser = null;
    this.userForm = { name: '', email: '' };
    this.showModal = true;
    this.clearMessages();
  }

  openEditModal(user: User): void {
    this.isEditing = true;
    this.currentUser = user;
    this.userForm = { name: user.name, email: user.email };
    this.showModal = true;
    this.clearMessages();
  }

  closeModal(): void {
    this.showModal = false;
    this.currentUser = null;
    this.userForm = { name: '', email: '' };
    this.clearMessages();
  }

  saveUser(): void {
    if (!this.userForm.name || !this.userForm.email) {
      this.showError('Por favor, preencha todos os campos');
      return;
    }

    if (this.isEditing && this.currentUser) {
      this.userService.updateUser(this.currentUser.id, this.userForm).subscribe({
        next: () => {
          this.showSuccess('Usuário atualizado com sucesso!');
          this.closeModal();
          this.loadUsers();
        },
        error: (error) => {
          this.showError('Erro ao atualizar usuário: ' + (error.error?.error || error.message));
        }
      });
    } else {
      this.userService.createUser(this.userForm).subscribe({
        next: () => {
          this.showSuccess('Usuário criado com sucesso!');
          this.closeModal();
          this.loadUsers();
        },
        error: (error) => {
          this.showError('Erro ao criar usuário: ' + (error.error?.error || error.message));
        }
      });
    }
  }

  deleteUser(id: number): void {
    if (confirm('Tem certeza que deseja excluir este usuário?')) {
      this.userService.deleteUser(id).subscribe({
        next: () => {
          this.showSuccess('Usuário excluído com sucesso!');
          this.loadUsers();
        },
        error: (error) => {
          this.showError('Erro ao excluir usuário: ' + (error.error?.error || error.message));
        }
      });
    }
  }

  showError(message: string): void {
    this.errorMessage = message;
    this.successMessage = '';
    setTimeout(() => this.clearMessages(), 5000);
  }

  showSuccess(message: string): void {
    this.successMessage = message;
    this.errorMessage = '';
    setTimeout(() => this.clearMessages(), 5000);
  }

  clearMessages(): void {
    this.errorMessage = '';
    this.successMessage = '';
  }
}

<div class="container">
  <div class="card">
    <h1>CRUD de Usuários</h1>
    <p>Gerenciamento de usuários com Angular e Node.js</p>

    <button class="btn btn-primary" (click)="openCreateModal()">
      + Novo Usuário
    </button>
  </div>

  <div class="card" *ngIf="errorMessage">
    <div class="alert alert-error">{{ errorMessage }}</div>
  </div>

  <div class="card" *ngIf="successMessage">
    <div class="alert alert-success">{{ successMessage }}</div>
  </div>

  <div class="card">
    <h2>Lista de Usuários</h2>
    <table class="table" *ngIf="users.length > 0; else noUsers">
      <thead>
        <tr>
          <th>ID</th>
          <th>Nome</th>
          <th>Email</th>
          <th>Ações</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let user of users">
          <td>{{ user.id }}</td>
          <td>{{ user.name }}</td>
          <td>{{ user.email }}</td>
          <td>
            <button class="btn btn-secondary" (click)="openEditModal(user)" style="margin-right: 8px;">
              Editar
            </button>
            <button class="btn btn-danger" (click)="deleteUser(user.id)">
              Excluir
            </button>
          </td>
        </tr>
      </tbody>
    </table>
    <ng-template #noUsers>
      <p>Nenhum usuário cadastrado.</p>
    </ng-template>
  </div>
</div>

<!-- Modal -->
<div class="modal" [class.show]="showModal" (click)="closeModal()">
  <div class="modal-content" (click)="$event.stopPropagation()">
    <div class="modal-header">
      <h2>{{ isEditing ? 'Editar Usuário' : 'Novo Usuário' }}</h2>
      <button class="close" (click)="closeModal()">&times;</button>
    </div>

    <form (ngSubmit)="saveUser()">
      <div class="form-group">
        <label for="name">Nome:</label>
        <input
          type="text"
          id="name"
          [(ngModel)]="userForm.name"
          name="name"
          required
        />
      </div>

      <div class="form-group">
        <label for="email">Email:</label>
        <input
          type="email"
          id="email"
          [(ngModel)]="userForm.email"
          name="email"
          required
        />
      </div>

      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" (click)="closeModal()">
          Cancelar
        </button>
        <button type="submit" class="btn btn-primary">
          {{ isEditing ? 'Atualizar' : 'Criar' }}
        </button>
      </div>
    </form>
  </div>
</div>
