import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { UserService } from '../../services/user.service';
import { AuthService } from '../../services/auth.service';
import { User } from '../../models/user.model';
import { fadeInOut, slideUp, slideIn } from '../../animations';

@Component({
  selector: 'app-dashboard',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './dashboard.component.html',
  styleUrl: './dashboard.component.css',
  animations: [fadeInOut, slideUp, slideIn]
})
export class DashboardComponent implements OnInit {
  users: User[] = [];
  currentUser: User | null = null;
  showModal = false;
  isEditing = false;
  errorMessage = '';
  successMessage = '';
  currentUserInfo: any;

  userForm = {
    name: '',
    email: ''
  };

  constructor(
    private userService: UserService,
    private authService: AuthService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.currentUserInfo = this.authService.getUser();
    this.loadUsers();
  }

  loadUsers(): void {
    this.userService.getAllUsers().subscribe({
      next: (users) => {
        this.users = users;
        this.clearMessages();
      },
      error: (error) => {
        if (error.status === 401) {
          this.authService.logout();
          this.router.navigate(['/login']);
        } else {
          this.showError('Erro ao carregar usu√°rios: ' + (error.error?.error || error.message));
        }
      }
    });
  }

  openCreateModal(): void {
    this.isEditing = false;
    this.currentUser = null;
    this.userForm = { name: '', email: '' };
    this.showModal = true;
    this.clearMessages();
  }

  openEditModal(user: User): void {
    this.isEditing = true;
    this.currentUser = user;
    this.userForm = { name: user.name, email: user.email };
    this.showModal = true;
    this.clearMessages();
  }

  closeModal(): void {
    this.showModal = false;
    this.currentUser = null;
    this.userForm = { name: '', email: '' };
    this.clearMessages();
  }

  saveUser(): void {
    if (!this.userForm.name || !this.userForm.email) {
      this.showError('Por favor, preencha todos os campos');
      return;
    }

    if (this.isEditing && this.currentUser) {
      this.userService.updateUser(this.currentUser.id, this.userForm).subscribe({
        next: () => {
          this.showSuccess('Usu√°rio atualizado com sucesso!');
          this.closeModal();
          this.loadUsers();
        },
        error: (error) => {
          if (error.status === 401) {
            this.authService.logout();
            this.router.navigate(['/login']);
          } else {
            this.showError('Erro ao atualizar usu√°rio: ' + (error.error?.error || error.message));
          }
        }
      });
    } else {
      this.userService.createUser(this.userForm).subscribe({
        next: () => {
          this.showSuccess('Usu√°rio criado com sucesso!');
          this.closeModal();
          this.loadUsers();
        },
        error: (error) => {
          if (error.status === 401) {
            this.authService.logout();
            this.router.navigate(['/login']);
          } else {
            this.showError('Erro ao criar usu√°rio: ' + (error.error?.error || error.message));
          }
        }
      });
    }
  }

  deleteUser(id: number): void {
    if (confirm('Tem certeza que deseja excluir este usu√°rio?')) {
      this.userService.deleteUser(id).subscribe({
        next: () => {
          this.showSuccess('Usu√°rio exclu√≠do com sucesso!');
          this.loadUsers();
        },
        error: (error) => {
          if (error.status === 401) {
            this.authService.logout();
            this.router.navigate(['/login']);
          } else {
            this.showError('Erro ao excluir usu√°rio: ' + (error.error?.error || error.message));
          }
        }
      });
    }
  }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/login']);
  }

  showError(message: string): void {
    this.errorMessage = message;
    this.successMessage = '';
    setTimeout(() => this.clearMessages(), 5000);
  }

  showSuccess(message: string): void {
    this.successMessage = message;
    this.errorMessage = '';
    setTimeout(() => this.clearMessages(), 5000);
  }

  clearMessages(): void {
    this.errorMessage = '';
    this.successMessage = '';
  }
}

<div class="dashboard-container">
  <header class="dashboard-header">
    <div class="header-content">
      <div>
        <h1>CRUD de Usu√°rios</h1>
        <p class="subtitle">Gerenciamento completo de usu√°rios</p>
      </div>
      <div class="user-info">
        <span class="user-name">{{ currentUserInfo?.name }}</span>
        <button class="btn btn-logout" (click)="logout()">
          <span class="icon">üö™</span> Sair
        </button>
      </div>
    </div>
  </header>

  <div class="dashboard-content">
    <div class="alert alert-error" *ngIf="errorMessage" [@fadeInOut]>
      <span class="alert-icon">‚ö†Ô∏è</span>
      {{ errorMessage }}
    </div>

    <div class="alert alert-success" *ngIf="successMessage" [@fadeInOut]>
      <span class="alert-icon">‚úì</span>
      {{ successMessage }}
    </div>

    <div class="card card-header">
      <div class="card-header-content">
        <div>
          <h2>Usu√°rios Cadastrados</h2>
          <p class="card-subtitle">{{ users.length }} usu√°rio(s) no total</p>
        </div>
        <button class="btn btn-primary btn-icon" (click)="openCreateModal()">
          <span class="icon">+</span> Novo Usu√°rio
        </button>
      </div>
    </div>

    <div class="card" *ngIf="users.length > 0; else noUsers">
      <div class="table-container">
        <table class="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Nome</th>
              <th>Email</th>
              <th class="actions-column">A√ß√µes</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let user of users; let i = index" [@slideIn] [style.animation-delay]="i * 0.05 + 's'">
              <td class="id-cell">{{ user.id }}</td>
              <td class="name-cell">{{ user.name }}</td>
              <td class="email-cell">{{ user.email }}</td>
              <td class="actions-cell">
                <button class="btn btn-icon btn-edit" (click)="openEditModal(user)" title="Editar">
                  <span class="icon">‚úèÔ∏è</span>
                </button>
                <button class="btn btn-icon btn-delete" (click)="deleteUser(user.id)" title="Excluir">
                  <span class="icon">üóëÔ∏è</span>
                </button>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>

    <ng-template #noUsers>
      <div class="card empty-state">
        <div class="empty-state-content">
          <span class="empty-icon">üë•</span>
          <h3>Nenhum usu√°rio cadastrado</h3>
          <p>Comece criando seu primeiro usu√°rio</p>
          <button class="btn btn-primary" (click)="openCreateModal()">
            + Criar Primeiro Usu√°rio
          </button>
        </div>
      </div>
    </ng-template>
  </div>
</div>

<!-- Modal -->
<div class="modal" [class.show]="showModal" (click)="closeModal()" [@fadeInOut]>
  <div class="modal-content" (click)="$event.stopPropagation()" [@slideUp]>
    <div class="modal-header">
      <h2>{{ isEditing ? 'Editar Usu√°rio' : 'Novo Usu√°rio' }}</h2>
      <button class="close" (click)="closeModal()">&times;</button>
    </div>

    <form (ngSubmit)="saveUser()">
      <div class="form-group">
        <label for="name">Nome</label>
        <input
          type="text"
          id="name"
          [(ngModel)]="userForm.name"
          name="name"
          placeholder="Digite o nome"
          required
        />
      </div>

      <div class="form-group">
        <label for="email">Email</label>
        <input
          type="email"
          id="email"
          [(ngModel)]="userForm.email"
          name="email"
          placeholder="Digite o email"
          required
        />
      </div>

      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" (click)="closeModal()">
          Cancelar
        </button>
        <button type="submit" class="btn btn-primary">
          {{ isEditing ? 'Atualizar' : 'Criar' }}
        </button>
      </div>
    </form>
  </div>
</div>
