import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../../services/auth.service';
import { fadeInScale, slideUp, fadeInOut } from '../../animations';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './login.component.html',
  styleUrl: './login.component.css',
  animations: [fadeInScale, slideUp, fadeInOut]
})
export class LoginComponent {
  isLoginMode = true;
  loading = false;
  errorMessage = '';

  loginForm = {
    email: '',
    password: ''
  };

  registerForm = {
    name: '',
    email: '',
    password: '',
    confirmPassword: ''
  };

  constructor(
    private authService: AuthService,
    private router: Router
  ) {}

  toggleMode(): void {
    this.isLoginMode = !this.isLoginMode;
    this.errorMessage = '';
    this.loginForm = { email: '', password: '' };
    this.registerForm = { name: '', email: '', password: '', confirmPassword: '' };
  }

  login(): void {
    if (!this.loginForm.email || !this.loginForm.password) {
      this.errorMessage = 'Por favor, preencha todos os campos';
      return;
    }

    this.loading = true;
    this.errorMessage = '';

    this.authService.login(this.loginForm).subscribe({
      next: () => {
        this.router.navigate(['/dashboard']);
      },
      error: (error) => {
        this.errorMessage = error.error?.error || 'Erro ao fazer login';
        this.loading = false;
      }
    });
  }

  register(): void {
    if (!this.registerForm.name || !this.registerForm.email || !this.registerForm.password) {
      this.errorMessage = 'Por favor, preencha todos os campos';
      return;
    }

    if (this.registerForm.password !== this.registerForm.confirmPassword) {
      this.errorMessage = 'As senhas não coincidem';
      return;
    }

    if (this.registerForm.password.length < 6) {
      this.errorMessage = 'A senha deve ter no mínimo 6 caracteres';
      return;
    }

    this.loading = true;
    this.errorMessage = '';

    this.authService.register({
      name: this.registerForm.name,
      email: this.registerForm.email,
      password: this.registerForm.password
    }).subscribe({
      next: () => {
        // Após registro, fazer login automaticamente
        this.authService.login({
          email: this.registerForm.email,
          password: this.registerForm.password
        }).subscribe({
          next: () => {
            this.router.navigate(['/dashboard']);
          },
          error: (error) => {
            this.errorMessage = 'Registro realizado, mas erro ao fazer login: ' + (error.error?.error || error.message);
            this.loading = false;
          }
        });
      },
      error: (error) => {
        this.errorMessage = error.error?.error || 'Erro ao registrar';
        this.loading = false;
      }
    });
  }
}

<div class="login-container">
  <div class="login-card">
    <div class="login-header" [@fadeInScale]>
      <h1>{{ isLoginMode ? 'Bem-vindo' : 'Criar Conta' }}</h1>
      <p>{{ isLoginMode ? 'Faça login para continuar' : 'Preencha os dados para criar sua conta' }}</p>
    </div>

    <div class="alert alert-error" *ngIf="errorMessage" [@fadeInOut]>
      <span class="alert-icon">⚠️</span>
      {{ errorMessage }}
    </div>

    <!-- Formulário de Login -->
    <form *ngIf="isLoginMode" (ngSubmit)="login()" class="login-form">
      <div class="form-group">
        <label for="loginEmail">Email</label>
        <input
          type="email"
          id="loginEmail"
          [(ngModel)]="loginForm.email"
          name="loginEmail"
          placeholder="Digite seu email"
          required
        />
      </div>

      <div class="form-group">
        <label for="loginPassword">Senha</label>
        <input
          type="password"
          id="loginPassword"
          [(ngModel)]="loginForm.password"
          name="loginPassword"
          placeholder="Digite sua senha"
          required
        />
      </div>

      <button type="submit" class="btn btn-primary btn-block" [disabled]="loading">
        <span *ngIf="!loading">Entrar</span>
        <span *ngIf="loading" class="loading-content">
          <span class="spinner-small"></span>
          Carregando...
        </span>
      </button>
    </form>

    <!-- Formulário de Registro -->
    <form *ngIf="!isLoginMode" (ngSubmit)="register()" class="login-form">
      <div class="form-group">
        <label for="name">Nome</label>
        <input
          type="text"
          id="name"
          [(ngModel)]="registerForm.name"
          name="name"
          placeholder="Digite seu nome"
          required
        />
      </div>

      <div class="form-group">
        <label for="registerEmail">Email</label>
        <input
          type="email"
          id="registerEmail"
          [(ngModel)]="registerForm.email"
          name="registerEmail"
          placeholder="Digite seu email"
          required
        />
      </div>

      <div class="form-group">
        <label for="registerPassword">Senha</label>
        <input
          type="password"
          id="registerPassword"
          [(ngModel)]="registerForm.password"
          name="registerPassword"
          placeholder="Digite sua senha"
          required
        />
      </div>

      <div class="form-group">
        <label for="confirmPassword">Confirmar Senha</label>
        <input
          type="password"
          id="confirmPassword"
          [(ngModel)]="registerForm.confirmPassword"
          name="confirmPassword"
          placeholder="Confirme sua senha"
          required
        />
      </div>

      <button type="submit" class="btn btn-primary btn-block" [disabled]="loading">
        <span *ngIf="!loading">Criar Conta</span>
        <span *ngIf="loading" class="loading-content">
          <span class="spinner-small"></span>
          Carregando...
        </span>
      </button>
    </form>

    <div class="login-footer">
      <p>
        {{ isLoginMode ? 'Não tem uma conta?' : 'Já tem uma conta?' }}
        <a (click)="toggleMode()" class="link">{{ isLoginMode ? 'Criar conta' : 'Fazer login' }}</a>
      </p>
    </div>
  </div>
</div>
